import React, { useRef, useState } from 'react'
import './App.css'
import { useEffect } from 'react'


const App = () => {
const [player,setPlayer]= useState({isTrue:false,duration:0})
const [currentTime,SetcurrentTime] =useState(0)

const audioPlayer = useRef()
const inputRef = useRef()
const animationRef = useRef()
useEffect(()=>{

 const maxDuration=  Math.floor(audioPlayer.current.duration)

 inputRef.current.max = maxDuration

  setPlayer(pre=>{
    return{...pre,duration:maxDuration}
  })

  if (inputRef.current.value===inputRef.current.max) {
    setPlayer(pre=>{
      return{...pre,isTrue:false}
    })

  }


},[inputRef.current.value,audioPlayer?.current?.onloadedmetadata,audioPlayer?.current?.readyState])



const start =()=>{
  const {current} =audioPlayer 
  setPlayer(pre=>{
    return{...pre,isTrue:!pre.isTrue}
  })
  if (player.isTrue) {
    current.pause() 
    cancelAnimationFrame(animationRef.current)

  }else{
    current.play()
   animationRef.current=requestAnimationFrame(whilePlayer)
  }




}

const whilePlayer= ()=>{
  inputRef.current.value = audioPlayer.current.currentTime
inputRef.current.style.setProperty('--seek-before-width',`${inputRef.current.value/player.duration*100}%`)
SetcurrentTime(inputRef.current.value)
animationRef.current=requestAnimationFrame(whilePlayer)

}



const minSecond =(Second)=>{

  const sec = Math.floor(Second%60)
  const min = Math.floor(Second/60)

 const returnSec = sec<10 ? `0${sec}` : sec
 const returnMin = min<10 ? `0${min}` : min

 return` ${returnMin}:${returnSec}`
}


const ChangeRange =()=>{

audioPlayer.current.currentTime = inputRef.current.value

inputRef.current.style.setProperty('--seek-before-width',`${inputRef.current.value/player.duration*100}%`,)
SetcurrentTime(inputRef.current.value)
}

const abc= ()=>{

}
audioPlayer.current.currentTime = inputRef.current.value

inputRef.current.style.setProperty('--seek-before-width',`${inputRef.current.value/player.duration*100}%`,)
SetcurrentTime(inputRef.current.value)


  return (
    <div className='a'>



<audio   ref={audioPlayer}  >

  <source  src='BLACK VEIL BRIDES - Scarlet Cross (Official Music Video).mp3' type='audio/mpeg'/>
   </audio>

<div className='song'>
<label>{minSecond(currentTime)}</label>
<input type='range' id='range' defaultValue='0' ref={inputRef} onChange={ChangeRange}/>
<label>{player.duration>=0&& minSecond(player.duration)}</label>
</div>
<div className='btn'>
<button onClick={()=>start()}>{player.isTrue?'pause':'play'} </button>
<button onClick={()=>abc()}>ileri30</button>
<button>geri30</button>
</div>
    </div>
  )
}

export default App
