import React, { useEffect, useRef, useState } from 'react'
import'./denme.css'

const Deneme = () => {
const  [playStop,setPlayStop] =useState(false)
const [duration,setDuration]= useState(0)
const playRef = useRef()
const RangeRef = useRef()

useEffect(()=>{
  const maxDuration = Math.floor(playRef.current.duration)
  setDuration(maxDuration)
RangeRef.current.max =maxDuration

  start()

},[playRef?.current?.onloadedmetadata,playRef?.current?.readyState])

const secMinValue = (second)=>{
const min = Math.floor( second/60)
const sec = Math.floor( second%60)

const  returnMin = min<10 ?`0${min}`:min 
const  returnSec = sec<10 ?`0${sec}`:sec 

return `${returnMin}:${returnSec}`

}

const start=()=>{
  setPlayStop(pre=>!pre)
  const Duration= Math.floor(playRef?.current?.duration)

  setDuration(Duration)


if (playStop) {
  playRef?.current.play()
}else{
  playRef?.current.pause()
}

}
const HandleChange =()=>{
playRef.current.currentTime =RangeRef?.current?.value
  
}
const whilePlayer=()=>{
  RangeRef.current.value= 
RangeRef.current.style.setProperty('--seek-before-width',`${RangeRef.current.value/duration*100}%`)

}

  return (


  <div className='main'>
<audio ref={playRef} controls>
<source src='BLACK VEIL BRIDES - Scarlet Cross (Official Music Video).mp3' type='audio/mpeg'/>

</audio>

<div className='range-p'>
  <p>{secMinValue(playRef?.current?.currentTime)}</p>
<input ref={RangeRef} className='range' defaultValue='0' type='range' onChange={HandleChange}/>
 {duration>=0&& <p>{ secMinValue(duration)}</p>}
  

</div>



<button onClick={()=>start()}>{playStop?'play':'pause'}</button>
    </div>
  )
}

export default Deneme
