import React, { useEffect, useRef, useState } from 'react'
import'./denme.css'

const Deneme = () => {
const  [playStop,setPlayStop] =useState(true)
const playRef = useRef()
const RangeRef = useRef()

useEffect(()=>{
  start()

},[playRef?.current?.onloadedmetadata])

const start=()=>{
  setPlayStop(pre=>!pre)

if (playStop) {
  playRef?.current.play()
}else{
  playRef?.current.pause()
}

}
const HandleChange =()=>{
playRef.current.currentTime =RangeRef?.current?.va
  
}


  return (


  <div className='main'>
<audio ref={playRef} controls>
<source src='BLACK VEIL BRIDES - Scarlet Cross (Official Music Video).mp3' type='audio/mpeg'/>

</audio>

<div className='range-p'>
  <p>00:00</p>
<input ref={RangeRef} className='range' defaultValue='0' type='range' onChange={HandleChange}/>
  <p>00:50</p>

</div>



<button onClick={()=>start()}>{playStop?'play':'pause'}</button>
    </div>
  )
}

export default Deneme
